mapper.select:
   	SELECT *
   	FROM  (
   		  SELECT ROWNUM R, Q.* 
   		  FROM (
				SELECT * 
				FROM QNA

				ORDER BY REF DESC,STEP ASC
				) Q
			)
	WHERE R BETWEEN #{startRow} AND #{lastRow}

mapper.insert:
   		INSERT INTO
   		QNA(
   			BOARD_NUM
   		  , BOARD_WRITER
   		  , BOARD_TITLE
   		  , BOARD_CONTENTS
   		  ,	CREATE_DATE
   		  , UPDATE_DATE
   		  , BOARD_CATEGORY
   		  , REF
   		  , STEP
   		  , DEPTH
   		    )
		VALUES(
				NOTICE_SEQ.NEXTVAL
			 ,	#{board_writer}
			 ,	#{board_title}
			 ,	#{board_contents}
			 , SYSDATE
			 , SYSDATE
			 , 'Q'
			 ,	#{ref}
			 ,	#{step}
			 ,	#{depth}
			  )

mapper.update:
   	UPDATE QNA 
	SET STEP = STEP +1
	WHERE REF = #{ref} 
				AND
				STEP > #{step}

UPDATE QNA
SET 
	 BOARD_TITLE = #{board_title}
	,BOARD_CONTENTS = #{board_contents}
	,UPDATE_DATE = SYSDATE
WHERE BOARD_NUM = ${board_num}

 UPDATE QNA 
 SET BOARD_HIT = BOARD_HIT + 1
	WHERE BOARD_NUM = #{board_num}
